sys_variable_value.value:
(function execute(inputs, outputs) {

  var result = new x_58056_rf.RequestFulfillment().getWaitForDetails(inputs.reqItemGR, inputs.activityID);
  outputs.activity_required = result.condition;
  outputs.activity_display = result.display;
  outputs.subtype = result.subType; 
  outputs.wait_for_condition = result.waitCondition;
  outputs.duration = new GlideDuration(result.duration);
  outputs.enable_timeout = result.enableTimeout;
  outputs.schedule = result.schedule;

})(inputs, outputs);

