sys_script.script:
(function executeRule(current, previous /*null when async*/) {
  var displayName = current.type.toString() == "approval" ? current.subtype.getDisplayValue() + " " : "";
  displayName += current.type.getDisplayValue();
  if (current.type.toString() == "approval") {
    displayName += current.subtype.toString() == "manager" ? " " : ": ";
    if (!current.groups.nil()) {
      displayName += current.groups.getDisplayValue();
    } else if (!current.users.nil()) {
      displayName += current.users.getDisplayValue();
    }
  } else if (current.type.toString() == "task") {
    displayName += ": ";
    if (!current.short_description.nil()) {
      displayName += current.short_description.toString();
    }
  } else if (current.type.toString() == "integration") {
    displayName += ": ";
    if (!current.integration_flow.nil()) {
      displayName += current.integration_flow.getDisplayValue();
    }
  } else if (current.type.toString() == "subflow") {
    displayName += ": ";
    if (!current.subflow.nil()) {
      displayName += current.subflow.getDisplayValue();
    }
  } else if (current.type.toString() == "workflow") {
    displayName += ": ";
    if (!current.run_workflow.nil()) {
      displayName += current.run_workflow.getDisplayValue();
    }
  } else if (current.type.toString() == "wait_for") {
    displayName += ": " + current.subtype.getDisplayValue();
  } else if (current.type.toString() == "flow_designer") {
    displayName = current.subtype.getDisplayValue() + ": ";
	if (! current.run_subflow.nil()) {
      displayName += current.run_subflow.getDisplayValue();
	} else {
      displayName += current.run_action.getDisplayValue();
	}
  }
  current.display_name = displayName;
})(current, previous);
